describe("jasmine.NestedResults",function(){it("#addResult increments counters",function(){var e=new jasmine.NestedResults;e.addResult(new jasmine.ExpectationResult({matcherName:"foo",passed:!0,message:"Passed.",actual:"bar",expected:"bar"})),expect(e.getItems().length).toEqual(1),expect(e.totalCount).toEqual(1),expect(e.passedCount).toEqual(1),expect(e.failedCount).toEqual(0),e.addResult(new jasmine.ExpectationResult({matcherName:"baz",passed:!1,message:"FAIL.",actual:"corge",expected:"quux"})),expect(e.getItems().length).toEqual(2),expect(e.totalCount).toEqual(2),expect(e.passedCount).toEqual(1),expect(e.failedCount).toEqual(1)}),it("should roll up counts for nested results",function(){var e=new jasmine.NestedResults;e.addResult(new jasmine.ExpectationResult({matcherName:"toSomething",passed:!0,message:"message",actual:"",expected:""})),e.addResult(new jasmine.ExpectationResult({matcherName:"toSomethingElse",passed:!1,message:"message",actual:"a",expected:"b"}));var t=new jasmine.NestedResults;t.addResult(new jasmine.ExpectationResult({matcherName:"toSomething",passed:!0,message:"message",actual:"",expected:""})),t.addResult(new jasmine.ExpectationResult({matcherName:"toSomethineElse",passed:!1,message:"message",actual:"c",expected:"d"}));var n=new jasmine.NestedResults;n.addResult(e),n.addResult(t),expect(n.getItems().length).toEqual(2),expect(n.totalCount).toEqual(4),expect(n.passedCount).toEqual(2),expect(n.failedCount).toEqual(2)})});